freg <- function(
    calculate = "selectpower",
    regmod = "rsqdev",
    r2 = 0.01,
    k = 1,
    m = 1,
    power = 0.8,
    alpha = 0.05,
    n = 200) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("freg requires jmvcore to be installed (restart may be required)")


    options <- fregOptions$new(
        calculate = calculate,
        regmod = regmod,
        r2 = r2,
        k = k,
        m = m,
        power = power,
        alpha = alpha,
        n = n)

    analysis <- fregClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

